con:config.script:
def mockService = testRunner.testCase.testSuite.project.mockServices["HighLoadCallbackRecipient"];

def properties = mockService.getPropertyNames();
for (propertyName in properties)
	mockService.removeProperty(propertyName);

mockService.start();

def mockService = testRunner.testCase.testSuite.project.mockServices["HighLoadCallbackRecipient"];
def killTC = testRunner.testCase.testSteps["TryKillHighwaySB"];

mockService.getMockRunner().stop();
assert mockService.getPropertyCount().toString() == killTC.getPropertyValue("SendedMessageCount");

import com.eviware.soapui.support.XmlHolder;

def msgTypes = new XmlHolder(testRunner.testCase.testSteps["GetMsgTypesFromBus"].getPropertyValue("Response"));

if (msgTypes["//*:id"] == testRunner.testCase.testSuite.getPropertyValue("HighLoadMessageTestTypeID")) {
	testRunner.gotoStepByName("Exit");
} else if (testRunner.testCase.getPropertyValue("DidUpdate") == "false") {
	testRunner.testCase.setPropertyValue("DidUpdate", "true");
	testRunner.gotoStepByName("UpdateClientSubscribesForMsgs");
} else {
	testRunner.gotoStepByName("SubscribeClientForMessageCallback");
}

def response = testRunner.testCase.testSteps["SendMessageToESB"].getPropertyValue("Response");
def sendedMessageCount = new Integer(testRunner.testCase.getPropertyValue("SendedMessageCount"));
def maxSendMessageCount = new Integer(testRunner.testCase.getPropertyValue("MaxSendMessageCount"));
def messageAccepted = false;

try {
	new com.eviware.soapui.support.XmlHolder(response);
	messageAccepted = true;
} catch (e) {}

if (messageAccepted && sendedMessageCount < maxSendMessageCount) {
	testRunner.testCase.setPropertyValue("SendedMessageCount", (++sendedMessageCount).toString());
	testRunner.gotoStepByName("SendMessageToESB");
} else if (messageAccepted || sendedMessageCount > maxSendMessageCount) {
	assert true == false;
}

def response = testRunner.testCase.testSteps["IsUp"].getPropertyValue("Response");
def waitingCount = new Integer(testRunner.testCase.getPropertyValue("WaitingCount"));
def maxWaitingCount = new Integer(testRunner.testCase.getPropertyValue("MaxWaitingCount"));
def isAlive = false;

try {
	def xmlHolder = new com.eviware.soapui.support.XmlHolder(response);
	isAlive = xmlHolder["//*:IsUpResult"] == "true";
} catch (e) {}

if (!isAlive && waitingCount < maxWaitingCount) {
	testRunner.testCase.setPropertyValue("WaitingCount", (++waitingCount).toString());
	testRunner.gotoStepByName("JustWaiting");
} else if (!isAlive) {
	assert true == false;
}

def response = testRunner.testCase.testSteps["GetCurrentThisTypeMessageCount"].getPropertyValue("Response");
def waitingCount = new Integer(testRunner.testCase.getPropertyValue("WaitingCount"));
def maxWaitingCount = new Integer(testRunner.testCase.getPropertyValue("MaxWaitingCount"));
def xmlResponse = new com.eviware.soapui.support.XmlHolder(response);
def count = new Integer(xmlResponse["//*:GetCurrentThisTypeMessageCountResult"]);

if (count > 0 && waitingCount < maxWaitingCount) {
	testRunner.testCase.setPropertyValue("WaitingCount", (++waitingCount).toString());
	testRunner.gotoStepByName("WaitingMessageDelivery");
} else if (count > 0) {
	assert true == false;
}

con:mockService.con:onRequestScript:
def currentDate = new Date().format("yyyy-MM-dd");
def request = new com.eviware.soapui.support.XmlHolder(mockRequest.requestContent);

request.namespaces["b"] = "http://schemas.datacontract.org/2004/07/IIS.Persona.ServiceBus.Objects";

switch (request["//b:Body"]) {
	case "Приоритетное сообщение. Тестирование ${currentDate}.":
		mockRunner.mockService.setPropertyValue("PriorityMessageReceived", "true");
		break;
	case "Сообщение с тегами. Тестирование ${currentDate}.":
		mockRunner.mockService.setPropertyValue("TaggedMessageReceived", "true");
		break;
	case "Сообщение которое будет затерто. Тестирование ${currentDate}.":
		mockRunner.mockService.setPropertyValue("FirstGroupMessageReceived", "true");
		break;
	case "Это сообщение затирает предыдущее сообщение с группой. Тестирование ${currentDate}.":
		mockRunner.mockService.setPropertyValue("LastGroupMessageReceived", "true");
		break;
	case "Сообщение. Тестирование ${currentDate}.":
		mockRunner.mockService.setPropertyValue("MessageReceived", "true");
		break;
	default:
		mockRunner.mockService.setPropertyValue("UnknownMessageReceived", "true");
		break;
}
